import { useState } from "react"
import styled from "styled-components"

export default function Cadastro() {

    const [form, setForm] = useState({
        nome: '',
        email: '',
        foto: '',
        biografia: '',
        senha: ''
    })

    function handleForm(e) {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        })
    }

    return (
        <CadastroPagina>
            <div>
                <h1>Cadastro</h1>
            </div>
            <CadastroContainer>
                <form>
                    <input
                        type="text"
                        value={form.nome}
                        onChange={e => handleForm(e)}
                        placeholder="Nome" />
                    <input
                        type="text"
                        onChange={handleForm}
                        value={form.email}
                        placeholder="E-mail" />
                    <input
                        type="text"
                        onChange={handleForm}
                        value={form.foto}
                        placeholder="Foto de Perfil" />
                    <textarea
                        type="text"
                        onChange={handleForm}
                        value={form.biografia}
                        placeholder="Biografia (atÃ© 200 caracteres)" />
                    <input
                        type="password"
                        onChange={handleForm}
                        value={form.senha}
                        placeholder="Senha" />
                    <input
                        type="password"
                        placeholder="Confirme sua senha" />
                    <button type="button" onClick={()=>alert(form)}><h1>Cadastrar!</h1></button>
                </form>
            </CadastroContainer>
        </CadastroPagina>
    )
}

const CadastroPagina = styled.main`
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
    color: black;
}
`

const CadastroContainer = styled.div`
width: 70%;
height: 80%;
box-sizing: border-box;
border: 2px solid black;
display: flex;
flex-direction: column;
justify-content: space-evenly;
input {
    width: 80%;
}
textarea{
    height: 30%;
    width: 80%;
}
`
