import axios from "axios"
import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import styled from "styled-components"
import UnidadePost from "../components/UnidadePost"

export default function Perfil() {
    const url = `http://localhost:5000`
    const {id} = useParams()

    const [infos, setInfos]=useState({nome: "Carregando", biografia:"Carregando", foto:"https://i.redd.it/7iyjin6f64561.jpg"})
    const [postagens, setPostagens]=useState([])
    const navigate = useNavigate()
    const { sessao } = useContext(UsuarioContext)

    useEffect(()=> {
        const teste = axios.get(`${url}/perfil/${id}`)
        teste.then((res)=> {
            console.log(res.data)
setInfos(res.data[0])
console.log(res.data[0])
setPostagens(res.data[1])
console.log(res.data[1])
        })
        teste.catch((err) => alert("vsf"))
    }, [])

function seguirEste(){
      const config = { headers: { Authorization: `Bearer ${sessao}` } }
      const seguir = axios.post(`${url}/seguir/${id}`, config)
      seguir.then((res)=> {
        console.log(res.data)
      })
      seguir.catch((err) => alert("vsf"))
}


    return (
        <PaginaInicial>
            <Detalhes>
                <Imagem>
                    <Circulo >
                        <img src={infos.foto} alt="foto" />
                    </Circulo>
                </Imagem>

                <Informações>
                    <h1>{infos.nome}
                    </h1>
                    <p>
                        {infos.biografia}
                    </p>
                    <div>
                        <button onClick={seguirEste}>Seguir!</button>
                    </div>
                </Informações>
            </Detalhes>
            <PostsPerfil>
                {postagens.map((post, index) => (
                    <UnidadePost
                        foto={post.foto_post}
                        descrição={post.descrição}
                    />
                ))}

            </PostsPerfil>
        </PaginaInicial>
    )
}
const PaginaInicial = styled.main` 
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
color: black;
}
`



const Detalhes = styled.div`
display: flex;
width: 60%;
height: 25%;
border: 3px solid black;
box-sizing: border-box;
`
const Imagem = styled.div`
width: 30%;
height: 100%;
background-color: pink;
display: flex;
justify-content: center;
align-items: center;
`

const Circulo = styled.div`
width: 180px;
height: 180px;
border-radius: 50%;
border: 3px solid black;
overflow: hidden;
box-sizing: border-box;
img{
height: 190%;
}
`
const Informações = styled.div`
padding: 2% 5%;
display: flex;
flex-direction: column;
justify-content: space-around;
div{
width: 50%;
margin-top: 4%;
display: flex;
justify-content: space-between;
}
`
const PostsPerfil = styled.div`
width: 60%;
height: 70%;
overflow-y: scroll;
`
