import { styled } from "styled-components"

export default function UnidadePost({foto, descrição, id, postador}){
const body = {id: id, postador: postador}
function fazIsso(){
    alert(body)
}
    return (
        <Post>
            <div className="imagem">
                <img src={foto}/>
            </div>
            <div className="detalhes">
                <p onClick={fazIsso}> <ion-icon name="heart"></ion-icon> {id} pessoas custiram isso!</p>
                <p>20/05/2023 ás 14h32</p>
            </div>
            <div className="descrição">
                <p>{descrição}</p>
            </div>
        </Post>
    )
}

const Post = styled.div`
width: 100%;
height: 60vh;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
border: 3px solid black;
box-sizing: border-box;
margin-bottom: 5%;
.imagem{
    width: 80%;
    height: 65%;
    border: 3px solid black;
    box-sizing: border-box;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}
img{
    height: 120%;
}
.detalhes {
    width: 80%;
    height: 6%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.descrição{
    width: 80%;
    height: 20%;
}
`
