import { useContext } from "react"
import { useNavigate } from "react-router-dom"
import styled from "styled-components"
import { UsuarioContext } from "../contexts/UsuarioContext"
import axios from "axios"

export default function Header(){
    const url = `http://localhost:5000`
    const { sessao } = useContext(UsuarioContext)

    const navigate = useNavigate()
    function paginaInicial(){
        navigate("/")
    }
function pesquisar(){
    navigate("/busca")
}

function verPerfil(){
    navigate("/eu")
}

function sair(){
    const config = { headers: { Authorization: `Bearer ${sessao}` } }
    const sair = axios.delete(`${url}/sair/sessoes`, config)
    navigate("/entrar")
}

    return (
        <HeaderEstilizado>
        <div onClick={paginaInicial}>
            <h1>Gatálogo</h1>
        </div>
        <div className="opções">
            <div className="icone" onClick={pesquisar}> 
            <p>Pesquisar Usuários</p>
            <ion-icon name="search-circle-outline"></ion-icon>
            </div>
            <div className="icone" onClick={verPerfil}>
                <p>Ir para meu perfil</p>
                <ion-icon name="person-outline"></ion-icon>
            </div>
            <div className="icone" onClick={sair}>
                <p>Sair da minha conta</p>
                <ion-icon name="walk-outline"></ion-icon></div>
            </div>
        </HeaderEstilizado>
    )
}

const HeaderEstilizado = styled.header`
width: 100%;
height: 10%;
background-color: lavenderblush;
display: flex;
align-items: center;
justify-content:space-between;
border: 2px solid black;
box-sizing: border-box;
h1{
    margin-left: 10%;
    color: black;
}
.opções{
    width: 14%;
    display: flex;
    justify-content: space-evenly;
    background-color: aliceblue;
}
.icone{
    width: 35%;
    height: 100%;
    margin-right: 2%;
    display: flex;
    flex-direction: column;
    align-items: center;
}
ion-icon{
    font-size: 40px;
}
`
