import { styled } from "styled-components"
import { UsuarioContext } from "../contexts/UsuarioContext"
import { useContext, useEffect, useState } from "react"
import axios from "axios"

export default function PostFeed({avatar, nome, foto, descrição, id, postador, id_postador}){
const body = {id: id, postador: postador}
const [curtidas, setCurtidas] = useState("carregando...")
const url = `http://localhost:5000`
const { sessao } = useContext(UsuarioContext)



function fazIsso(){
    console.log(body)
    const config = { headers: { Authorization: `Bearer ${sessao}` } }
const curte = axios.post(`${url}/curtir`, body, config)
curte.then((res) => console.log(res.data))
curte.catch((err)=> console.log(err.message))
}
useEffect(()=> {
const carregacurtidas = axios.get(`${url}/curtir/${id}`)
carregacurtidas.then((res) => setCurtidas(res.data.length))
carregacurtidas.catch((err)=> console.log(err.message))
}, [curtidas])


    return (
        <Post>
            <div className="avatar" key={id}>   <Circulo > <img src={avatar} alt="avatar"/>    </Circulo><h1>{nome}</h1></div>
            <div className="imagem" >
                <img src={foto}/>
            </div>
            <div className="detalhes">
                <p onClick={fazIsso}> <ion-icon name="heart"></ion-icon> {id} {curtidas} pessoas custiram isso!</p>
                <p>20/05/2023 ás 14h32</p>
            </div>
            <div className="descrição">
                <p>{descrição}</p>
            </div>
        </Post>
    )
}

const Post = styled.div`
width: 100%;
height: 60vh;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
border: 3px solid black;
box-sizing: border-box;
margin-bottom: 5%;
.avatar{
    width: 100%;
    height: 15%;
    display: flex;
    align-items: center;
    background-color: plum;
}
.imagem{
    width: 80%;
    height: 55%;
    border: 3px solid black;
    box-sizing: border-box;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;

    img{
    height: 120%;
}
}
.detalhes {
    width: 80%;
    height: 6%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.descrição{
    width: 80%;
    height: 15%;
}
`


const Circulo = styled.div`
width: 80px;
margin-left: 2%;
margin-right: 2%;
height: 80px;
border-radius: 50%;
border: 3px solid black;
overflow: hidden;
box-sizing: border-box;
img{
   height: 120%;
}
`
