import axios from "axios"
import { useState } from "react"
import { Navigate } from "react-router-dom"
import styled from "styled-components"



export default function Cadastro() {
    const url = `http://localhost:5000`


    const [form, setForm] = useState({ "nome": "", "email": "", "foto": "", "biografia": "", "senha": "" })

    function handleForm(e) {
        setForm({ ...form, [e.target.name]: e.target.value })
    }

    function cadastrarUsuario(e) {
        e.preventDefault()
        const body = form
        const cadastro = axios.post(`${url}/cadastro`, body)
        cadastro.then((res) => {
            alert(res.data)
        })
        cadastro.catch((err) => alert(err.response.message))
    }

    return (
        <CadastroPagina>
            <div>
                <h1>Cadastro</h1>
            </div>
            <CadastroContainer>
                <form onSubmit={cadastrarUsuario}>
                    <input
                        required
                        placeholder="Nome"
                        name="nome"
                        value={form.nome}
                        onChange={handleForm} />
                    <input
                        required
                        type="text"
                        placeholder="E-mail"
                        name="email"
                        value={form.email}
                        onChange={handleForm} />
                    <input
                        type="text"
                        placeholder="Foto de Perfil"
                        name="foto"
                        value={form.foto}
                        onChange={handleForm}
                    />
                    <textarea
                        type="text"
                        name="biografia"
                        onChange={handleForm}
                        value={form.biografia}
                        placeholder="Biografia (atÃ© 200 caracteres)" />
                    <input
                        type="password"
                        name="senha"
                        onChange={handleForm}
                        value={form.senha}
                        placeholder="Senha" />
                    <input
                        type="password"
                        placeholder="Confirme sua senha" />
                    <button type="submit" ><h1>Cadastrar!</h1></button>
                </form>
            </CadastroContainer>
        </CadastroPagina>
    )
}

const CadastroPagina = styled.main`
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
    color: black;
}
`

const CadastroContainer = styled.div`
width: 70%;
height: 80%;
box-sizing: border-box;
border: 2px solid black;
display: flex;
flex-direction: column;
justify-content: space-evenly;
input {
    width: 80%;
}
textarea{
    height: 30%;
    width: 80%;
}
`
