import { useState } from "react"
import styled from "styled-components"

export default function NovoPost() {

    const [form, setForm] = useState({ foto: '', descrição: '' })

    function handleForm(e) {
        setForm({ ...form, [e.target.name]: e.target.value })
    }

    function fazerNovaPostagem(){
        e.preventDefault()
        const body = form
        console.log(body)
    }

    return (
        <NovoPostPage>
            <div>
                <h1>
                    Novo Post
                </h1>
            </div>
            <NovoPostContainer>
                <form onSubmit={fazerNovaPostagem}>
                    <input
                        required
                        name="foto"
                        value={form.foto}
                        onChange={handleForm}
                        placeholder="Foto"
                    />
                    <input
                        type="Textarea"
                        name="descrição"
                        value={form.descrição}
                        onChange={handleForm}
                        placeholder="Descrição"
                    />
                    <button>Criar Post</button>
                </form>
            </NovoPostContainer>
        </NovoPostPage>
    )
}

const NovoPostPage = styled.main`
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
    color: black;
}
`

const NovoPostContainer = styled.div`
width: 70%;
height: 80%;
box-sizing: border-box;
border: 2px solid black;
display: flex;
flex-direction: column;
justify-content: space-evenly;
input {
    width: 80%;
}
`
