import styled from "styled-components"
import Seguidor from "../components/Seguidor"
import axios from "axios"
import { useEffect, useState } from "react"

export default function Busca(){
    const url = `http://localhost:5000`

    const [search, setSearch]=useState("")

    const searchLowerCase = search.toLocaleLowerCase()

    const [usuarios, setUsuarios]=useState([{ foto: "https://i.pinimg.com/originals/ba/92/7f/ba927ff34cd961ce2c184d47e8ead9f6.jpg",
     nome: "Caralho", biografia:"QUero q vc se foda", id:"7"}, { foto: "https://i.pinimg.com/originals/ba/92/7f/ba927ff34cd961ce2c184d47e8ead9f6.jpg",
     nome: "Fodaase", biografia:"QUero q vc se foda", id:"7"}, { foto: "https://i.pinimg.com/originals/ba/92/7f/ba927ff34cd961ce2c184d47e8ead9f6.jpg",
     nome: "Babidi", biografia:"QUero q vc se foda", id:"7"}])

const pesquisador = usuarios.filter((usuario) => usuario.nome.toLocaleLowerCase().includes(searchLowerCase));

    return (
        <PaginaSeguidores>
        <div className="titulo">
            <input type="search"
            placeholder="Pesquise aqui usuarios"
            name="search"
            value={search}
            onChange={e => setSearch(e.target.value)}/>
        </div>
        <ContainerSeguidores>

        {pesquisador.map((s) => (
                    <Seguidor
                        nome={s.nome}
                        foto={s.foto}
                        biografia={s.biografia}
                        id={s.seguidor}
                    />
                ))}


        </ContainerSeguidores>
    </PaginaSeguidores>
)
            }

