import styled from "styled-components"
import UnidadePost from "../components/UnidadePost"
import { useContext, useEffect, useState } from "react"
import { UsuarioContext } from "../contexts/UsuarioContext"
import axios from "axios"

export default function Inicial() {
    const url = `http://localhost:5000`
    const { sessao } = useContext(UsuarioContext)
    const [infos, setInfos]=useState()

    useEffect(()=> {
        const config = { headers: { Authorization: `Bearer ${sessao}` } }
const carregai = axios.get(`${url}/me/posts`, config)
carregai.then((res)=> {
console.log(res.data)
setInfos(res.data[0])
})
carregai.catch((err) => console.log(err.response.message))

    }, [])
    return (
        <PaginaInicial>
            <Detalhes>
                <Imagem>
                    <Circulo></Circulo>
                </Imagem>

                <Informações>
                    <h1> {infos.nome}
                    </h1>
                    <p>
                        Me chamo Fulane de Tal meus pronomes são "manda o pix".
                        Gosto muito de ser um gatinho fofinho
                        SOu a coisa mais linda que existe, sem meme.
                    </p>
                    <div>
                        <button>Ver seguidores</button>        <button>Ver quem segue</button>
                    </div>
                </Informações>
            </Detalhes>
            <PostsPerfil>
<UnidadePost />
<UnidadePost />
<UnidadePost />
            </PostsPerfil>
        </PaginaInicial>
    )
}
const PaginaInicial = styled.main` 
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
    color: black;
}
`

const Detalhes = styled.div`
display: flex;
width: 60%;
height: 25%;
border: 3px solid black;
box-sizing: border-box;
`
const Imagem = styled.div`
width: 30%;
height: 100%;
background-color: pink;
display: flex;
justify-content: center;
align-items: center;
`

const Circulo = styled.div`
width: 180px;
height: 180px;
border-radius: 50%;
background-image: url(https://img.freepik.com/fotos-premium/gato-fofo-de-olhos-grandes-gatinho-gato-fofo-fofo_303714-151.jpg);
background-size: cover;
border: 3px solid red;
box-sizing: border-box;
`
const Informações = styled.div`
padding: 2% 5%;
display: flex;
flex-direction: column;
justify-content: space-around;
div{
    width: 50%;
    margin-top: 4%;
    display: flex;
    justify-content: space-between;
}
`
const PostsPerfil = styled.div`
width: 60%;
height: 70%;
overflow-y: scroll;
`
