import axios from "axios"
import { useState } from "react"
import styled from "styled-components"

export default function Perfil() {
    const url = `http://localhost:5000`

    const [infos, setInfos]=useState({nome: "Carregando", biografia:"Carregando", foto:"https://i.redd.it/7iyjin6f64561.jpg"})
    const [postagens, setPostagens]=useState([])
    const navigate=useNavigate()

    useEffect(()=> {
        const carregai = axios.get(`${url}/perfil/:id`, )
carregai.then((res)=> {
console.log(res.data)
setInfos(res.data[0])
console.log(res.data[0])
setPostagens(res.data[1])
console.log(res.data[1])
})
carregai.catch((err) => console.log(err.response.message))

    }, [])

    return (
        <PaginaInicial>
            <Detalhes>
                <Imagem>
                    <Circulo >
                        <img src={infos.foto} alt="foto" />
                    </Circulo>
                </Imagem>

                <Informações>
                    <h1>{infos.nome}
                    </h1>
                    <p>
                        {infos.biografia}
                    </p>
                    <div>
                        <button onClick={seguir}>Seguir!</button>
                    </div>
                </Informações>
            </Detalhes>
            <PostsPerfil>
                {postagens.map((post, index) => (
                    <UnidadePost
                        foto={post.foto_post}
                        descrição={post.descrição}
                    />
                ))}

            </PostsPerfil>
            <BotaoAdicionar onClick={navegar}><ion-icon name="add-circle-outline"></ion-icon></BotaoAdicionar>
        </PaginaInicial>
    )
}
const PaginaInicial = styled.main` 
width: 100%;
height: 90%;
display: flex;
flex-direction: column;
align-items: center;
justify-content: space-evenly;
h1{
color: black;
}
position: relative
`
const BotaoAdicionar = styled.div`
position: absolute;
bottom: 6%;
right: 10%;
width: 60px;
height: 60px;
border-radius: 50%;
background-color: black;
display: flex;
align-items: center;
justify-content: center;
ion-icon{
font-size: 300px;
color: white;
}
`

const Detalhes = styled.div`
display: flex;
width: 60%;
height: 25%;
border: 3px solid black;
box-sizing: border-box;
`
const Imagem = styled.div`
width: 30%;
height: 100%;
background-color: pink;
display: flex;
justify-content: center;
align-items: center;
`

const Circulo = styled.div`
width: 180px;
height: 180px;
border-radius: 50%;
border: 3px solid black;
overflow: hidden;
box-sizing: border-box;
img{
height: 190%;
}
`
const Informações = styled.div`
padding: 2% 5%;
display: flex;
flex-direction: column;
justify-content: space-around;
div{
width: 50%;
margin-top: 4%;
display: flex;
justify-content: space-between;
}
`
const PostsPerfil = styled.div`
width: 60%;
height: 70%;
overflow-y: scroll;
`
